#' @param rowTitleText .
#' @param biplotTitleText .
#' @param titleAlign .
#' @param titleFontSize .
#' @param titleFontFace .
#' @param columnSubtitleText .
#' @param rowSubtitleText .
#' @param biplotSubtitleText .
#' @param subtitleAlign .
#' @param subtitleFontSize .
#' @param subtitleFontFace .
#' @param rowCaptionText .
#' @param columnCaptionText .
#' @param biplotCaptionText .
#' @param captionAlign .
#' @param captionFontSize .
#' @param captionFontFace .
#' @param xAxisText .
#' @param xAxisFontSize .
#' @param xAxisPosition .
#' @param yAxisText .
#' @param yAxisFontSize .
#' @param yAxisPosition .
#' @param descAsVarName .
#' @return A results object containing:
#' \tabular{llllll}{
#'   \code{results$helpMessage} \tab \tab \tab \tab \tab a html \cr
#'   \code{results$contingency} \tab \tab \tab \tab \tab a table \cr
#'   \code{results$rowProfiles} \tab \tab \tab \tab \tab a table \cr
#'   \code{results$colProfiles} \tab \tab \tab \tab \tab a table \cr
#'   \code{results$eigenvalues} \tab \tab \tab \tab \tab a table \cr
#'   \code{results$rowSummary} \tab \tab \tab \tab \tab a table \cr
#'   \code{results$colSummary} \tab \tab \tab \tab \tab a table \cr
#'   \code{results$rowplot} \tab \tab \tab \tab \tab an image \cr
#'   \code{results$colplot} \tab \tab \tab \tab \tab an image \cr
#'   \code{results$biplot} \tab \tab \tab \tab \tab an image \cr
#' }
#'
#' Tables can be converted to data frames with \code{asDF} or \code{\link{as.data.frame}}. For example:
#'
#' \code{results$contingency$asDF}
#'
#' \code{as.data.frame(results$contingency)}
#'
#' @export
corresp <- function(
    data,
    rows,
    cols,
    counts,
    showContingency = FALSE,
    showProfiles = FALSE,
    showInertia = TRUE,
    showSummaries = FALSE,
    showRowPlot = FALSE,
    showColPlot = FALSE,
    showBiPlot = TRUE,
    supplementaryRows,
    supplementaryCols,
    dimNum = 2,
    xaxis = 1,
    yaxis = 2,
    normalization = "principal",
    plotWidth = 600,
    plotHeight = 450,
    labelSize = 12,
    rowColor = "blue",
    colColor = "red",
    supColor = "darkgray",
    columnTitleText = "default",
    rowTitleText = "default",
    biplotTitleText = "default",
    titleAlign = "0.5",
    titleFontSize = 16,
    titleFontFace = "plain",
    columnSubtitleText = "default",
    rowSubtitleText = "default",
    biplotSubtitleText = "default",
    subtitleAlign = "0.5",
    subtitleFontSize = 12,
    subtitleFontFace = "italic",
    rowCaptionText,
    columnCaptionText,
    biplotCaptionText,
    captionAlign = "1",
    captionFontSize = 12,
    captionFontFace = "plain",
    xAxisText,
    xAxisFontSize = "16",
    xAxisPosition = "0.5",
    yAxisText,
    yAxisFontSize = "16",
    yAxisPosition = "0.5",
    descAsVarName = FALSE) {

    if ( ! requireNamespace("jmvcore", quietly=TRUE))
        stop("corresp requires jmvcore to be installed (restart may be required)")

    if ( ! missing(rows)) rows <- jmvcore::resolveQuo(jmvcore::enquo(rows))
    if ( ! missing(cols)) cols <- jmvcore::resolveQuo(jmvcore::enquo(cols))
    if ( ! missing(counts)) counts <- jmvcore::resolveQuo(jmvcore::enquo(counts))
    if (missing(data))
        data <- jmvcore::marshalData(
            parent.frame(),
            `if`( ! missing(rows), rows, NULL),
            `if`( ! missing(cols), cols, NULL),
            `if`( ! missing(counts), counts, NULL))

    for (v in rows) if (v %in% names(data)) data[[v]] <- as.factor(data[[v]])
    for (v in cols) if (v %in% names(data)) data[[v]] <- as.factor(data[[v]])

    options <- correspOptions$new(
        rows = rows,
        cols = cols,
        counts = counts,
        showContingency = showContingency,
        showProfiles = showProfiles,
        showInertia = showInertia,
        showSummaries = showSummaries,
        showRowPlot = showRowPlot,
        showColPlot = showColPlot,
        showBiPlot = showBiPlot,
        supplementaryRows = supplementaryRows,
        supplementaryCols = supplementaryCols,
        dimNum = dimNum,
        xaxis = xaxis,
        yaxis = yaxis,
        normalization = normalization,
        plotWidth = plotWidth,
        plotHeight = plotHeight,
        labelSize = labelSize,
        rowColor = rowColor,
        colColor = colColor,
        supColor = supColor,
        columnTitleText = columnTitleText,
        rowTitleText = rowTitleText,
        biplotTitleText = biplotTitleText,
        titleAlign = titleAlign,
        titleFontSize = titleFontSize,
        titleFontFace = titleFontFace,
        columnSubtitleText = columnSubtitleText,
        rowSubtitleText = rowSubtitleText,
        biplotSubtitleText = biplotSubtitleText,
        subtitleAlign = subtitleAlign,
        subtitleFontSize = subtitleFontSize,
        subtitleFontFace = subtitleFontFace,
        rowCaptionText = rowCaptionText,
        columnCaptionText = columnCaptionText,
        biplotCaptionText = biplotCaptionText,
        captionAlign = captionAlign,
        captionFontSize = captionFontSize,
        captionFontFace = captionFontFace,
        xAxisText = xAxisText,
        xAxisFontSize = xAxisFontSize,
        xAxisPosition = xAxisPosition,
        yAxisText = yAxisText,
        yAxisFontSize = yAxisFontSize,
        yAxisPosition = yAxisPosition,
        descAsVarName = descAsVarName)

    analysis <- correspClass$new(
        options = options,
        data = data)

    analysis$run()

    analysis$results
}

