multcorrespBase <- if (requireNamespace("jmvcore", quietly=TRUE)) R6::R6Class(
    "multcorrespBase",
    inherit = jmvcore::Analysis,
    public = list(
        initialize = function(options, data=NULL, datasetId="", analysisId="", revision=0) {
            super$initialize(
                package = "vijPlots",
                name = "multcorresp",
                version = c(1,0,0),
                options = options,
                results = multcorrespResults$new(options=options),
                data = data,
                datasetId = datasetId,
                analysisId = analysisId,
                revision = revision,
                pause = NULL,
                completeWhenFilled = FALSE,
                requiresMissings = FALSE,
                weightsSupport = 'none')
        }))

#' Multiple Correspondence Analysis
#'
#' 
#' @param data .
#' @param vars .
#' @param supplVars .
#' @param labelVar .
#' @param method .
#' @param BenzecriAdj .
#' @param GreenacreAdj .
#' @param dimNum .
#' @param xaxis .
#' @param yaxis .
#' @param normalization .
#' @param showSummary .
#' @param showDiscriminations .
#' @param showCategories .
#' @param showObservations .
#' @param showDiscriminationPlot .
#' @param showCategoryPlot .
#' @param showObservationPlot .
#' @param showBiPlot .
#' @param discrimTitleText .
#' @param catTitleText .
#' @param obsTitleText .
#' @param biplotTitleText .
#' @param titleAlign .
#' @param titleFontSize .
#' @param titleFontFace .
#' @param discrimSubtitleText .
#' @param catSubtitleText .
#' @param obsSubtitleText .
#' @param biplotSubtitleText .
#' @param subtitleAlign .
#' @param subtitleFontSize .
#' @param subtitleFontFace .
#' @param discrimCaptionText .
#' @param catCaptionText .
#' @param obsCaptionText .
#' @param biplotCaptionText .
#' @param captionAlign .
#' @param captionFontSize .
#' @param captionFontFace .
#' @param discrimWidth .
#' @param discrimHeight .
#' @param catWidth .
#' @param catHeight .
#' @param obsWidth .
#' @param obsHeight .
#' @param biplotWidth .
#' @param biplotHeight .
#' @param obsColor .
#' @param colorPalette .
#' @param labelSize .
#' @param connectOrdinalCat .
#' @param propPoint .
#' @param ggrepel .
#' @param boldCat .
#' @param descAsVarName .
#' @param legendText .
#' @param legendFontSize .
#' @param legendPosition .
#' @param xAxisText .
#' @param xAxisFontSize .
#' @param xAxisPosition .
#' @param yAxisText .
#' @param yAxisFontSize .
#' @param yAxisPosition .
#' @return A results object containing:
#' \tabular{llllll}{
#'   \code{results$helpMessage} \tab \tab \tab \tab \tab a html \cr
#'   \code{results$eigenvalues} \tab \tab \tab \tab \tab a table \cr
#'   \code{results$discrim} \tab \tab \tab \tab \tab a table \cr
#'   \code{results$categories} \tab \tab \tab \tab \tab a table \cr
#'   \code{results$observations} \tab \tab \tab \tab \tab a table \cr
#'   \code{results$discrimplot} \tab \tab \tab \tab \tab an image \cr
#'   \code{results$categoryplot} \tab \tab \tab \tab \tab an image \cr
#'   \code{results$obsplot} \tab \tab \tab \tab \tab an image \cr
#'   \code{results$biplot} \tab \tab \tab \tab \tab an image \cr
#'   \code{results$obsCoordOV} \tab \tab \tab \tab \tab an output \cr
#' }
#'
#' Tables can be converted to data frames with \code{asDF} or \code{\link{as.data.frame}}. For example:
#'
#' \code{results$eigenvalues$asDF}
#'
#' \code{as.data.frame(results$eigenvalues)}
#'
#' @export
multcorresp <- function(
    data,
    vars,
    supplVars,
    labelVar,
    method = "Indicator",
    BenzecriAdj = FALSE,
    GreenacreAdj = FALSE,
    dimNum = 2,
    xaxis = 1,
    yaxis = 2,
    normalization = "principal",
    showSummary = TRUE,
    showDiscriminations = FALSE,
    showCategories = TRUE,
    showObservations = FALSE,
    showDiscriminationPlot = FALSE,
    showCategoryPlot = TRUE,
    showObservationPlot = FALSE,
    showBiPlot = FALSE,
    discrimTitleText = "default",
    catTitleText = "default",
    obsTitleText = "default",
    biplotTitleText = "default",
    titleAlign = "0.5",
    titleFontSize = 16,
    titleFontFace = "plain",
    discrimSubtitleText,
    catSubtitleText = "default",
    obsSubtitleText = "default",
    biplotSubtitleText = "default",
    subtitleAlign = "0.5",
    subtitleFontSize = 12,
    subtitleFontFace = "plain",
    discrimCaptionText,
    catCaptionText,
    obsCaptionText,
    biplotCaptionText,
    captionAlign = "1",
    captionFontSize = 12,
    captionFontFace = "plain",
    discrimWidth = 600,
    discrimHeight = 600,
    catWidth = 650,
    catHeight = 600,
    obsWidth = 600,
    obsHeight = 600,
    biplotWidth = 650,
    biplotHeight = 600,
    obsColor = "black",
    colorPalette = "jmv",
    labelSize = 12,
    connectOrdinalCat = FALSE,
    propPoint = FALSE,
    ggrepel = FALSE,
    boldCat = FALSE,
    descAsVarName = FALSE,
    legendText,
    legendFontSize = "14",
    legendPosition = "right",
    xAxisText,
    xAxisFontSize = "16",
    xAxisPosition = "0.5",
    yAxisText,
    yAxisFontSize = "16",
    yAxisPosition = "0.5") {

    if ( ! requireNamespace("jmvcore", quietly=TRUE))
        stop("multcorresp requires jmvcore to be installed (restart may be required)")

    if ( ! missing(vars)) vars <- jmvcore::resolveQuo(jmvcore::enquo(vars))
    if ( ! missing(supplVars)) supplVars <- jmvcore::resolveQuo(jmvcore::enquo(supplVars))
    if ( ! missing(labelVar)) labelVar <- jmvcore::resolveQuo(jmvcore::enquo(labelVar))
    if (missing(data))
        data <- jmvcore::marshalData(
            parent.frame(),
            `if`( ! missing(vars), vars, NULL),
            `if`( ! missing(supplVars), supplVars, NULL),
            `if`( ! missing(labelVar), labelVar, NULL))

    for (v in vars) if (v %in% names(data)) data[[v]] <- as.factor(data[[v]])
    for (v in supplVars) if (v %in% names(data)) data[[v]] <- as.factor(data[[v]])

    options <- multcorrespOptions$new(
        vars = vars,
        supplVars = supplVars,
        labelVar = labelVar,
        method = method,
        BenzecriAdj = BenzecriAdj,
        GreenacreAdj = GreenacreAdj,
        dimNum = dimNum,
        xaxis = xaxis,
        yaxis = yaxis,
        normalization = normalization,
        showSummary = showSummary,
        showDiscriminations = showDiscriminations,
        showCategories = showCategories,
        showObservations = showObservations,
        showDiscriminationPlot = showDiscriminationPlot,
        showCategoryPlot = showCategoryPlot,
        showObservationPlot = showObservationPlot,
        showBiPlot = showBiPlot,
        discrimTitleText = discrimTitleText,
        catTitleText = catTitleText,
        obsTitleText = obsTitleText,
        biplotTitleText = biplotTitleText,
        titleAlign = titleAlign,
        titleFontSize = titleFontSize,
        titleFontFace = titleFontFace,
        discrimSubtitleText = discrimSubtitleText,
        catSubtitleText = catSubtitleText,
        obsSubtitleText = obsSubtitleText,
        biplotSubtitleText = biplotSubtitleText,
        subtitleAlign = subtitleAlign,
        subtitleFontSize = subtitleFontSize,
        subtitleFontFace = subtitleFontFace,
        discrimCaptionText = discrimCaptionText,
        catCaptionText = catCaptionText,
        obsCaptionText = obsCaptionText,
        biplotCaptionText = biplotCaptionText,
        captionAlign = captionAlign,
        captionFontSize = captionFontSize,
        captionFontFace = captionFontFace,
        discrimWidth = discrimWidth,
        discrimHeight = discrimHeight,
        catWidth = catWidth,
        catHeight = catHeight,
        obsWidth = obsWidth,
        obsHeight = obsHeight,
        biplotWidth = biplotWidth,
        biplotHeight = biplotHeight,
        obsColor = obsColor,
        colorPalette = colorPalette,
        labelSize = labelSize,
        connectOrdinalCat = connectOrdinalCat,
        propPoint = propPoint,
        ggrepel = ggrepel,
        boldCat = boldCat,
        descAsVarName = descAsVarName,
        legendText = legendText,
        legendFontSize = legendFontSize,
        legendPosition = legendPosition,
        xAxisText = xAxisText,
        xAxisFontSize = xAxisFontSize,
        xAxisPosition = xAxisPosition,
        yAxisText = yAxisText,
        yAxisFontSize = yAxisFontSize,
        yAxisPosition = yAxisPosition)

    analysis <- multcorrespClass$new(
        options = options,
        data = data)

    analysis$run()

    analysis$results
}

