#' Pie Chart
#'
#' 
#' @param data .
#' @param aVar .
#' @param facet .
#' @param donut .
#' @param labels .
#' @param labType .
#' @param overlap .
#' @param labOffset .
#' @param labSize .
#' @param colorPalette .
#' @param borderColor .
#' @param textColor .
#' @param accuracy .
#' @param plotWidth .
#' @param plotHeight .
#' @param facetBy .
#' @param facetNumber .
#' @param titleText .
#' @param titleFontFace .
#' @param titleFontSize .
#' @param titleAlign .
#' @param subtitleText .
#' @param subtitleFontFace .
#' @param subtitleFontSize .
#' @param subtitleAlign .
#' @param captionText .
#' @param captionFontFace .
#' @param captionFontSize .
#' @param captionAlign .
#' @param legendText .
#' @param legendFontSize .
#' @param legendPosition .
#' @return A results object containing:
#' \tabular{llllll}{
#'   \code{results$plot} \tab \tab \tab \tab \tab an image \cr
#' }
#'
#' @export
piechart <- function(
    data,
    aVar,
    facet,
    donut = FALSE,
    labels = "none",
    labType,
    overlap = FALSE,
    labOffset = 0,
    labSize = 12,
    colorPalette = "jmv",
    borderColor = "black",
    textColor = "auto",
    accuracy = "0.1",
    plotWidth = 0,
    plotHeight = 0,
    facetBy = "column",
    facetNumber = 1,
    titleText,
    titleFontFace = "bold",
    titleFontSize = "14",
    titleAlign = "0.5",
    subtitleText,
    subtitleFontFace = "plain",
    subtitleFontSize = "12",
    subtitleAlign = "0.5",
    captionText,
    captionFontFace = "italic",
    captionFontSize = "10",
    captionAlign = "1",
    legendText,
    legendFontSize = "14",
    legendPosition = "right") {

    if ( ! requireNamespace("jmvcore", quietly=TRUE))
        stop("piechart requires jmvcore to be installed (restart may be required)")

    if ( ! missing(aVar)) aVar <- jmvcore::resolveQuo(jmvcore::enquo(aVar))
    if ( ! missing(facet)) facet <- jmvcore::resolveQuo(jmvcore::enquo(facet))
    if (missing(data))
        data <- jmvcore::marshalData(
            parent.frame(),
            `if`( ! missing(aVar), aVar, NULL),
            `if`( ! missing(facet), facet, NULL))

    for (v in aVar) if (v %in% names(data)) data[[v]] <- as.factor(data[[v]])
    for (v in facet) if (v %in% names(data)) data[[v]] <- as.factor(data[[v]])

    options <- piechartOptions$new(
        aVar = aVar,
        facet = facet,
        donut = donut,
        labels = labels,
        labType = labType,
        overlap = overlap,
        labOffset = labOffset,
        labSize = labSize,
        colorPalette = colorPalette,
        borderColor = borderColor,
        textColor = textColor,
        accuracy = accuracy,
        plotWidth = plotWidth,
        plotHeight = plotHeight,
        facetBy = facetBy,
        facetNumber = facetNumber,
        titleText = titleText,
        titleFontFace = titleFontFace,
        titleFontSize = titleFontSize,
        titleAlign = titleAlign,
        subtitleText = subtitleText,
        subtitleFontFace = subtitleFontFace,
        subtitleFontSize = subtitleFontSize,
        subtitleAlign = subtitleAlign,
        captionText = captionText,
        captionFontFace = captionFontFace,
        captionFontSize = captionFontSize,
        captionAlign = captionAlign,
        legendText = legendText,
        legendFontSize = legendFontSize,
        legendPosition = legendPosition)

    analysis <- piechartClass$new(
        options = options,
        data = data)

    analysis$run()

    analysis$results
}

