principalBase <- if (requireNamespace("jmvcore", quietly=TRUE)) R6::R6Class(
    "principalBase",
    inherit = jmvcore::Analysis,
    public = list(
        initialize = function(options, data=NULL, datasetId="", analysisId="", revision=0) {
            super$initialize(
                package = "vijPlots",
                name = "principal",
                version = c(1,0,0),
                options = options,
                results = principalResults$new(options=options),
                data = data,
                datasetId = datasetId,
                analysisId = analysisId,
                revision = revision,
                pause = NULL,
                completeWhenFilled = FALSE,
                requiresMissings = FALSE,
                weightsSupport = 'none')
        }))

#' Principal Component Analysis
#'
#' 
#' @param data .
#' @param vars .
#' @param labelVar .
#' @param groupVar .
#' @param rotation .
#' @param dimNum .
#' @param xaxis .
#' @param yaxis .
#' @param stdVariables .
#' @param showSummary .
#' @param showKMO .
#' @param showLoadings .
#' @param showObservations .
#' @param showScreePlot .
#' @param showVarPlot .
#' @param showObsPlot .
#' @param showBiplot .
#' @param biplotType .
#' @param biplotLines .
#' @param biplotStretch .
#' @param biplotStretchFactor .
#' @param plotWidth .
#' @param plotHeight .
#' @param obsLabelSize .
#' @param varLabelSize .
#' @param pointSize .
#' @param varColor .
#' @param labelColor .
#' @param obsColor .
#' @param colorPalette .
#' @param screeTitleText .
#' @param varTitleText .
#' @param obsTitleText .
#' @param biplotTitleText .
#' @param titleAlign .
#' @param titleFontSize .
#' @param titleFontFace .
#' @param screeSubtitleText .
#' @param varSubtitleText .
#' @param obsSubtitleText .
#' @param biplotSubtitleText .
#' @param subtitleAlign .
#' @param subtitleFontSize .
#' @param subtitleFontFace .
#' @param screeCaptionText .
#' @param varCaptionText .
#' @param obsCaptionText .
#' @param biplotCaptionText .
#' @param captionAlign .
#' @param captionFontSize .
#' @param captionFontFace .
#' @param kaiser .
#' @param descAsVarName .
#' @param stdScores .
#' @param stdLoadings .
#' @param stataRotation .
#' @param legendText .
#' @param legendFontSize .
#' @param legendPosition .
#' @param xAxisText .
#' @param xAxisFontSize .
#' @param xAxisPosition .
#' @param yAxisText .
#' @param yAxisFontSize .
#' @param yAxisPosition .
#' @return A results object containing:
#' \tabular{llllll}{
#'   \code{results$helpMessage} \tab \tab \tab \tab \tab a html \cr
#'   \code{results$summaryTable} \tab \tab \tab \tab \tab a table \cr
#'   \code{results$kmoTable} \tab \tab \tab \tab \tab a table \cr
#'   \code{results$loadingTable} \tab \tab \tab \tab \tab a table \cr
#'   \code{results$obsTable} \tab \tab \tab \tab \tab a table \cr
#'   \code{results$screePlot} \tab \tab \tab \tab \tab an image \cr
#'   \code{results$varPlot} \tab \tab \tab \tab \tab an image \cr
#'   \code{results$obsPlot} \tab \tab \tab \tab \tab an image \cr
#'   \code{results$biPlot} \tab \tab \tab \tab \tab an image \cr
#'   \code{results$obsCoordOV} \tab \tab \tab \tab \tab an output \cr
#' }
#'
#' Tables can be converted to data frames with \code{asDF} or \code{\link{as.data.frame}}. For example:
#'
#' \code{results$summaryTable$asDF}
#'
#' \code{as.data.frame(results$summaryTable)}
#'
#' @export
principal <- function(
    data,
    vars,
    labelVar,
    groupVar,
    rotation = "none",
    dimNum = 2,
    xaxis = 1,
    yaxis = 2,
    stdVariables = TRUE,
    showSummary = TRUE,
    showKMO = FALSE,
    showLoadings = FALSE,
    showObservations = FALSE,
    showScreePlot = TRUE,
    showVarPlot = TRUE,
    showObsPlot = TRUE,
    showBiplot = FALSE,
    biplotType = "formPlot",
    biplotLines = FALSE,
    biplotStretch = FALSE,
    biplotStretchFactor = 1,
    plotWidth = 0,
    plotHeight = 0,
    obsLabelSize = 10,
    varLabelSize = 12,
    pointSize = 3,
    varColor = "#377EB8",
    labelColor = "none",
    obsColor = "#E41A1C",
    colorPalette = "jmv",
    screeTitleText = "default",
    varTitleText = "default",
    obsTitleText = "default",
    biplotTitleText = "default",
    titleAlign = "0.5",
    titleFontSize = 16,
    titleFontFace = "plain",
    screeSubtitleText,
    varSubtitleText = "default",
    obsSubtitleText = "default",
    biplotSubtitleText = "default",
    subtitleAlign = "0.5",
    subtitleFontSize = 12,
    subtitleFontFace = "plain",
    screeCaptionText,
    varCaptionText = "default",
    obsCaptionText = "default",
    biplotCaptionText,
    captionAlign = "1",
    captionFontSize = 12,
    captionFontFace = "plain",
    kaiser = FALSE,
    descAsVarName = FALSE,
    stdScores = FALSE,
    stdLoadings = FALSE,
    stataRotation = FALSE,
    legendText,
    legendFontSize = "14",
    legendPosition = "right",
    xAxisText,
    xAxisFontSize = "16",
    xAxisPosition = "0.5",
    yAxisText,
    yAxisFontSize = "16",
    yAxisPosition = "0.5") {

    if ( ! requireNamespace("jmvcore", quietly=TRUE))
        stop("principal requires jmvcore to be installed (restart may be required)")

    if ( ! missing(vars)) vars <- jmvcore::resolveQuo(jmvcore::enquo(vars))
    if ( ! missing(labelVar)) labelVar <- jmvcore::resolveQuo(jmvcore::enquo(labelVar))
    if ( ! missing(groupVar)) groupVar <- jmvcore::resolveQuo(jmvcore::enquo(groupVar))
    if (missing(data))
        data <- jmvcore::marshalData(
            parent.frame(),
            `if`( ! missing(vars), vars, NULL),
            `if`( ! missing(labelVar), labelVar, NULL),
            `if`( ! missing(groupVar), groupVar, NULL))

    for (v in groupVar) if (v %in% names(data)) data[[v]] <- as.factor(data[[v]])

    options <- principalOptions$new(
        vars = vars,
        labelVar = labelVar,
        groupVar = groupVar,
        rotation = rotation,
        dimNum = dimNum,
        xaxis = xaxis,
        yaxis = yaxis,
        stdVariables = stdVariables,
        showSummary = showSummary,
        showKMO = showKMO,
        showLoadings = showLoadings,
        showObservations = showObservations,
        showScreePlot = showScreePlot,
        showVarPlot = showVarPlot,
        showObsPlot = showObsPlot,
        showBiplot = showBiplot,
        biplotType = biplotType,
        biplotLines = biplotLines,
        biplotStretch = biplotStretch,
        biplotStretchFactor = biplotStretchFactor,
        plotWidth = plotWidth,
        plotHeight = plotHeight,
        obsLabelSize = obsLabelSize,
        varLabelSize = varLabelSize,
        pointSize = pointSize,
        varColor = varColor,
        labelColor = labelColor,
        obsColor = obsColor,
        colorPalette = colorPalette,
        screeTitleText = screeTitleText,
        varTitleText = varTitleText,
        obsTitleText = obsTitleText,
        biplotTitleText = biplotTitleText,
        titleAlign = titleAlign,
        titleFontSize = titleFontSize,
        titleFontFace = titleFontFace,
        screeSubtitleText = screeSubtitleText,
        varSubtitleText = varSubtitleText,
        obsSubtitleText = obsSubtitleText,
        biplotSubtitleText = biplotSubtitleText,
        subtitleAlign = subtitleAlign,
        subtitleFontSize = subtitleFontSize,
        subtitleFontFace = subtitleFontFace,
        screeCaptionText = screeCaptionText,
        varCaptionText = varCaptionText,
        obsCaptionText = obsCaptionText,
        biplotCaptionText = biplotCaptionText,
        captionAlign = captionAlign,
        captionFontSize = captionFontSize,
        captionFontFace = captionFontFace,
        kaiser = kaiser,
        descAsVarName = descAsVarName,
        stdScores = stdScores,
        stdLoadings = stdLoadings,
        stataRotation = stataRotation,
        legendText = legendText,
        legendFontSize = legendFontSize,
        legendPosition = legendPosition,
        xAxisText = xAxisText,
        xAxisFontSize = xAxisFontSize,
        xAxisPosition = xAxisPosition,
        yAxisText = yAxisText,
        yAxisFontSize = yAxisFontSize,
        yAxisPosition = yAxisPosition)

    analysis <- principalClass$new(
        options = options,
        data = data)

    analysis$run()

    analysis$results
}

