#' Q-Q & P-P Plots
#'
#' 
#' @param data .
#' @param dep .
#' @param group .
#' @param distrib .
#' @param transLog .
#' @param standardize .
#' @param refLine .
#' @param band .
#' @param methodQQ .
#' @param methodPP .
#' @param detrend .
#' @param type .
#' @param refType .
#' @param plotWidth .
#' @param plotHeight .
#' @param textSize .
#' @param paramMethod .
#' @param paramEstMethod .
#' @param param1 .
#' @param param2 .
#' @param titleText .
#' @param titleFontFace .
#' @param titleFontSize .
#' @param titleAlign .
#' @param subtitleText .
#' @param subtitleFontFace .
#' @param subtitleFontSize .
#' @param subtitleAlign .
#' @param captionText .
#' @param captionFontFace .
#' @param captionFontSize .
#' @param captionAlign .
#' @param legendText .
#' @param legendFontSize .
#' @param legendPosition .
#' @param xAxisText .
#' @param xAxisFontSize .
#' @param xAxisPosition .
#' @param yAxisText .
#' @param yAxisFontSize .
#' @param yAxisPosition .
#' @return A results object containing:
#' \tabular{llllll}{
#'   \code{results$helpMessage} \tab \tab \tab \tab \tab a html \cr
#'   \code{results$ErrorMessage} \tab \tab \tab \tab \tab a preformatted \cr
#'   \code{results$plot} \tab \tab \tab \tab \tab an image \cr
#'   \code{results$paramTable} \tab \tab \tab \tab \tab a table \cr
#' }
#'
#' Tables can be converted to data frames with \code{asDF} or \code{\link{as.data.frame}}. For example:
#'
#' \code{results$paramTable$asDF}
#'
#' \code{as.data.frame(results$paramTable)}
#'
#' @export
qqplot <- function(
    data,
    dep,
    group,
    distrib = "norm",
    transLog = FALSE,
    standardize = FALSE,
    refLine = TRUE,
    band = FALSE,
    methodQQ = "pointwise",
    methodPP = "ell",
    detrend = FALSE,
    type = "QQ",
    refType = "identity",
    plotWidth = 0,
    plotHeight = 0,
    textSize = 16,
    paramMethod = "paraEstimate",
    paramEstMethod = "mle",
    param1 = 0,
    param2 = 1,
    titleText = "default",
    titleFontFace = "bold",
    titleFontSize = "14",
    titleAlign = "0.5",
    subtitleText,
    subtitleFontFace = "plain",
    subtitleFontSize = "12",
    subtitleAlign = "0.5",
    captionText,
    captionFontFace = "italic",
    captionFontSize = "10",
    captionAlign = "1",
    legendText,
    legendFontSize = "14",
    legendPosition = "right",
    xAxisText,
    xAxisFontSize = "16",
    xAxisPosition = "0.5",
    yAxisText,
    yAxisFontSize = "16",
    yAxisPosition = "0.5") {

    if ( ! requireNamespace("jmvcore", quietly=TRUE))
        stop("qqplot requires jmvcore to be installed (restart may be required)")

    if ( ! missing(dep)) dep <- jmvcore::resolveQuo(jmvcore::enquo(dep))
    if ( ! missing(group)) group <- jmvcore::resolveQuo(jmvcore::enquo(group))
    if (missing(data))
        data <- jmvcore::marshalData(
            parent.frame(),
            `if`( ! missing(dep), dep, NULL),
            `if`( ! missing(group), group, NULL))

    for (v in group) if (v %in% names(data)) data[[v]] <- as.factor(data[[v]])

    options <- qqplotOptions$new(
        dep = dep,
        group = group,
        distrib = distrib,
        transLog = transLog,
        standardize = standardize,
        refLine = refLine,
        band = band,
        methodQQ = methodQQ,
        methodPP = methodPP,
        detrend = detrend,
        type = type,
        refType = refType,
        plotWidth = plotWidth,
        plotHeight = plotHeight,
        textSize = textSize,
        paramMethod = paramMethod,
        paramEstMethod = paramEstMethod,
        param1 = param1,
        param2 = param2,
        titleText = titleText,
        titleFontFace = titleFontFace,
        titleFontSize = titleFontSize,
        titleAlign = titleAlign,
        subtitleText = subtitleText,
        subtitleFontFace = subtitleFontFace,
        subtitleFontSize = subtitleFontSize,
        subtitleAlign = subtitleAlign,
        captionText = captionText,
        captionFontFace = captionFontFace,
        captionFontSize = captionFontSize,
        captionAlign = captionAlign,
        legendText = legendText,
        legendFontSize = legendFontSize,
        legendPosition = legendPosition,
        xAxisText = xAxisText,
        xAxisFontSize = xAxisFontSize,
        xAxisPosition = xAxisPosition,
        yAxisText = yAxisText,
        yAxisFontSize = yAxisFontSize,
        yAxisPosition = yAxisPosition)

    analysis <- qqplotClass$new(
        options = options,
        data = data)

    analysis$run()

    analysis$results
}

