#' Raincloud Plot
#'
#' 
#' @param data .
#' @param aVar .
#' @param groupOne .
#' @param groupTwo .
#' @param nudgeBoxplot .
#' @param horizontal .
#' @param reverse .
#' @param colorPalette .
#' @param plotWidth .
#' @param plotHeight .
#' @param alphaC .
#' @param titleText .
#' @param titleFontFace .
#' @param titleFontSize .
#' @param titleAlign .
#' @param subtitleText .
#' @param subtitleFontFace .
#' @param subtitleFontSize .
#' @param subtitleAlign .
#' @param captionText .
#' @param captionFontFace .
#' @param captionFontSize .
#' @param captionAlign .
#' @param legendText .
#' @param legendFontSize .
#' @param legendPosition .
#' @param xAxisText .
#' @param xAxisFontSize .
#' @param xAxisPosition .
#' @param yAxisText .
#' @param yAxisFontSize .
#' @param yAxisPosition .
#' @param yAxisLabelFontSize .
#' @param yAxisLabelRotation .
#' @param yAxisRangeType .
#' @param yAxisRangeMin .
#' @param yAxisRangeMax .
#' @param xAxisLabelFontSize .
#' @param xAxisLabelRotation .
#' @param xAxisRangeType .
#' @param xAxisRangeMin .
#' @param xAxisRangeMax .
#' @return A results object containing:
#' \tabular{llllll}{
#'   \code{results$plot} \tab \tab \tab \tab \tab an image \cr
#' }
#'
#' @export
raincloud <- function(
    data,
    aVar,
    groupOne,
    groupTwo,
    nudgeBoxplot = FALSE,
    horizontal = FALSE,
    reverse = FALSE,
    colorPalette = "jmv",
    plotWidth = 0,
    plotHeight = 0,
    alphaC = 0.5,
    titleText,
    titleFontFace = "bold",
    titleFontSize = "14",
    titleAlign = "0.5",
    subtitleText,
    subtitleFontFace = "plain",
    subtitleFontSize = "12",
    subtitleAlign = "0.5",
    captionText,
    captionFontFace = "italic",
    captionFontSize = "10",
    captionAlign = "1",
    legendText,
    legendFontSize = "14",
    legendPosition = "right",
    xAxisText,
    xAxisFontSize = "16",
    xAxisPosition = "0.5",
    yAxisText,
    yAxisFontSize = "16",
    yAxisPosition = "0.5",
    yAxisLabelFontSize = 12,
    yAxisLabelRotation = 0,
    yAxisRangeType = "auto",
    yAxisRangeMin = 0,
    yAxisRangeMax = 10,
    xAxisLabelFontSize = 12,
    xAxisLabelRotation = 0,
    xAxisRangeType = "auto",
    xAxisRangeMin = 0,
    xAxisRangeMax = 10) {

    if ( ! requireNamespace("jmvcore", quietly=TRUE))
        stop("raincloud requires jmvcore to be installed (restart may be required)")

    if ( ! missing(aVar)) aVar <- jmvcore::resolveQuo(jmvcore::enquo(aVar))
    if ( ! missing(groupOne)) groupOne <- jmvcore::resolveQuo(jmvcore::enquo(groupOne))
    if ( ! missing(groupTwo)) groupTwo <- jmvcore::resolveQuo(jmvcore::enquo(groupTwo))
    if (missing(data))
        data <- jmvcore::marshalData(
            parent.frame(),
            `if`( ! missing(aVar), aVar, NULL),
            `if`( ! missing(groupOne), groupOne, NULL),
            `if`( ! missing(groupTwo), groupTwo, NULL))

    for (v in groupOne) if (v %in% names(data)) data[[v]] <- as.factor(data[[v]])
    for (v in groupTwo) if (v %in% names(data)) data[[v]] <- as.factor(data[[v]])

    options <- raincloudOptions$new(
        aVar = aVar,
        groupOne = groupOne,
        groupTwo = groupTwo,
        nudgeBoxplot = nudgeBoxplot,
        horizontal = horizontal,
        reverse = reverse,
        colorPalette = colorPalette,
        plotWidth = plotWidth,
        plotHeight = plotHeight,
        alphaC = alphaC,
        titleText = titleText,
        titleFontFace = titleFontFace,
        titleFontSize = titleFontSize,
        titleAlign = titleAlign,
        subtitleText = subtitleText,
        subtitleFontFace = subtitleFontFace,
        subtitleFontSize = subtitleFontSize,
        subtitleAlign = subtitleAlign,
        captionText = captionText,
        captionFontFace = captionFontFace,
        captionFontSize = captionFontSize,
        captionAlign = captionAlign,
        legendText = legendText,
        legendFontSize = legendFontSize,
        legendPosition = legendPosition,
        xAxisText = xAxisText,
        xAxisFontSize = xAxisFontSize,
        xAxisPosition = xAxisPosition,
        yAxisText = yAxisText,
        yAxisFontSize = yAxisFontSize,
        yAxisPosition = yAxisPosition,
        yAxisLabelFontSize = yAxisLabelFontSize,
        yAxisLabelRotation = yAxisLabelRotation,
        yAxisRangeType = yAxisRangeType,
        yAxisRangeMin = yAxisRangeMin,
        yAxisRangeMax = yAxisRangeMax,
        xAxisLabelFontSize = xAxisLabelFontSize,
        xAxisLabelRotation = xAxisLabelRotation,
        xAxisRangeType = xAxisRangeType,
        xAxisRangeMin = xAxisRangeMin,
        xAxisRangeMax = xAxisRangeMax)

    analysis <- raincloudClass$new(
        options = options,
        data = data)

    analysis$run()

    analysis$results
}

